      marilynType: "",
      marilyndir: "",
      marilyncom: "",
      marilynnum: 1,
      marilynSelect: "",
      currentDelete: "",
      deleteModel: false,
      editModel: false,
      modal_loading: false,
      currentEdit: {},
      marilynNewImg: "",
      spinShow: true,
      labelId: [],
      proId: [],
      procon: [],
      typeId: [],
      classId: "",
      currentImg: null,
      addCard: false,
      dialogTableVisible: false,
      directionLists: [{
        value: "0",
        name: "竖屏"
      }, {
        value: "1",
        name: "横屏"
      }],
      commendLists: [{
        value: "0",
        name: "不推荐"
      }, {
        value: "1",
        name: "推荐"
      }]
    };
  },
  mounted: function mounted() {
    var _this = this;

    this.problemList();
    var judgename_add = document.querySelector(".judgename_add").querySelector("input");
    var judgename_edit = document.querySelector(".judgename_edit").querySelector("input");
    onfocus(judgename_add, function () {
      judgename_add.style.border = "1px solid #2d8cf0";
      _this.btnAdd = true;
    });
    onblur(judgename_add, function () {
      judgeName({
        name: _this.marilynName,
        type: "game"
      }).then(function (res) {
        if (res.state == 0) {
          if (_this.addCard) {
            _this.btnAdd = false;

            _this.$Message.error("名字为空或该名字已存在");

            judgename_add.style.border = "1px solid red";
            setTimeout(function () {
              judgename_add.style.border = "1px solid #dcdee2";
            }, 2000);
          } else {
            _this.btnAdd = true;
          }
        }
      });
    });
    onfocus(judgename_edit, function () {
      judgename_edit.style.border = "1px solid #2d8cf0";
      _this.btnEdit = true;
    });
    onblur(judgename_edit, function () {
      _this.btnEdit = true;
      judgeName({
        name: _this.currentEdit.name,
        type: "game",
        id: _this.currentDelete
      }).then(function (res) {
        if (res.state == 0) {
          if (_this.editModel) {
            _this.btnEdit = false;

            _this.$Message.error("名字为空或该名字已存在");

            judgename_edit.style.border = "1px solid red";
            setTimeout(function () {
              judgename_edit.style.border = "1px solid #dcdee2";
            }, 2000);
          }
        }
      });
    });
    this.zipuploadUrl = zipuploadUrl;
    this.gameImgurl = gameImgurl;
    this.gameuploadUri = gameuploadUri;
    this.getData();
    classIndex().then(function (res) {
      if (res.state === "1") {
        _this.classList = res.msg;
      }
    });
    labelIndex().then(function (res) {
      if (res.state === "1") {
        _this.tagLists = res.msg;
      }
    });
  },
  methods: {
    getpage: function getpage(res) {
      this.totaldatalist = res;
      this.datacount = this.totaldatalist.length;

      if (this.datacount < this.pagesize) {
        this.datalist = this.totaldatalist;
      } else {
        this.datalist = this.totaldatalist.slice(0, this.pagesize);
      }
    },
    getData: function getData() {
      var _this2 = this;

      this.spinShow = true;
      gamePending().then(function (res) {
        // console.log(res)
        if (res.state === "1") {
          _this2.spinShow = false;

          if (res.msg == "数据为空！") {
            _this2.getpage("");

            _this2.spinShow = true;
            _this2.procon = [];
          } else {
            var len = res.msg.length;

            for (var i = 0; i < len; i++) {
              //获取游戏分类
              if (_this2.gametype.indexOf(res.msg[i].class.name) == -1) {
                _this2.gametype.push(res.msg[i].class.name);
              }

              var tip_len = res.msg[i].label.length;

              for (var j = 0; j < tip_len; j++) {
                if (_this2.gametip.indexOf(res.msg[i].label[j].name) == -1) {
                  _this2.gametip.push(res.msg[i].label[j].name);
                }
              }

              _this2.procon.push(res.msg[i].tab);
            }

            _this2.getpage(res.msg); // console.log(this.procon)

          }
        } else {
          _this2.getpage("");

          _this2.spinShow = false;

          _this2.procon.push(res.msg[i].tab);
        }
      });
    },
    //判断联动
    connect: function connect() {
      this.connectarr = [this.showtype, this.showtip, this.showpingtai];
      var num = 0;

      for (var i = 0; i < 3; i++) {
        if (this.connectarr[i]) {
          num++;
        }
      }

      if (num >= 2) {
        this.connectflag = true;
      }
    },
    //获取联动数据
    connectResult: function connectResult() {
      var _this3 = this;

      // console.log(this.connectarr)
      // console.log("/////////////////////////////");
      var newarr = [];
      var that = this;
      gameIndex().then(function (res) {
        if (res.state === "1") {
          if (_this3.connectarr[0]) {
            //type
            if (newarr.length > 0) {
              newarr = newarr.filter(function (item) {
                if (item.class.name == that.connectarr[0]) {
                  return item;
                }
              });
            } else {
              newarr = res.msg.filter(function (item) {
                if (item.class.name == that.connectarr[0]) {
                  return item;
                }
              });
            }
          } // console.log(newarr);


          if (_this3.connectarr[1]) {
            //tip
            if (newarr.length > 0) {
              newarr = newarr.filter(function (item) {
                var len = item.label.length;

                if (len > 0) {
                  for (var j = 0; j < len; j++) {
                    if (item.label[j].name == that.connectarr[1]) {
                      return item;
                    }
                  }
                }
              });
            } else {
              newarr = res.msg.filter(function (item) {
                var len = item.label.length;

                if (len > 0) {
                  for (var j = 0; j < len; j++) {
                    if (item.label[j].name == that.connectarr[1]) {
                      return item;
                    }
                  }
                }
              });
            }
          } // console.log(newarr);


          if (_this3.connectarr[2]) {
            if (newarr.length > 0) {
              newarr = newarr.filter(function (item) {
                // console.log(i)
                if (that.connectarr[2] == "小程序") {
                  if (item.wechat == "1") {
                    return item;
                  }
                } else if (that.connectarr[2] == "安卓") {
                  if (item.android == "1") {
                    return item;
                  }
                } else if (that.connectarr[2] == "ios") {
                  if (item.ios == "1") {
                    return item;
                  }
                }
              });
            } else {
              newarr = res.msg.filter(function (item) {
                // console.log(i)
                if (that.connectarr[2] == "小程序") {
                  if (item.wechat == "1") {
                    return item;
                  }
                } else if (that.connectarr[2] == "安卓") {
                  if (item.android == "1") {
                    return item;
                  }
                } else if (that.connectarr[2] == "ios") {
                  if (item.ios == "1") {
                    return item;
                  }
                }
              });
            }
          } // console.log(newarr);


          _this3.getpage(newarr);
        }
      });
    },
    //全部游戏列表
    allgame: function allgame() {
      var _this4 = this;

      this.searchvalue = "";
      this.pageindex = 1;
      gamePending().then(function (res) {
        _this4.showtype = "";
        _this4.showtip = "";
        _this4.showpingtai = "";

        _this4.connect();

        _this4.connectflag = false;

        if (res.state === "1") {
          _this4.getpage(res.msg);

          _this4.spinShow = false;
        }
      });
    },
    //选择标签
    selecttip: function selecttip(i) {
      var _this5 = this;

      this.pageindex = 1; // this.showtype = [];

      this.showtip = i;
      this.connect(); // this.showpingtai=[]

      if (this.connectflag) {
        this.connectResult();
      } else {
        gamePending().then(function (res) {
          if (res.state === "1") {
            var newarr = res.msg.filter(function (item) {
              var len = item.label.length;

              if (len > 0) {
                for (var j = 0; j < len; j++) {
                  if (item.label[j].name == i) {
                    return item;
                  }
                }
              }
            });

            _this5.getpage(newarr); // this.savegamearr = newarr;

          }
        });
      }
    },
    //选择平台
    selectpigntai: function selectpigntai(i) {
      var _this6 = this;

      this.pageindex = 1;
      this.showpingtai = i;
      this.connect(); // this.showtype = [];
      // this.showtip = [];

      if (this.connectflag) {
        this.connectResult();
      } else {
        gamePending().then(function (res) {
          if (res.state === "1") {
            var newarr = res.msg.filter(function (item) {
              // console.log(i)
              if (i == "小程序") {
                if (item.wechat == "1") {
                  return item;
                }
              } else if (i == "安卓") {
                if (item.android == "1") {
                  return item;
                }
              } else if (i == "ios") {
                if (item.ios == "1") {
                  return item;
                }
              }
            });

            _this6.getpage(newarr);
          }
        });
      }
    },
    //选择分类
    selecttype: function selecttype(i) {
      var _this7 = this;

      this.pageindex = 1; // this.showtip = [];

      this.showtype = i;
      this.connect(); // this.showpingtai=[]

      if (this.connectflag) {
        this.connectResult();
      } else {
        gamePending().then(function (res) {
          if (res.state === "1") {
            var newarr = res.msg.filter(function (item) {
              if (item.class.name == i) {
                return item;
              }
            });

            _this7.getpage(newarr);
          }
        });
      }
    },
    markGame: function markGame(i) {
      this.markid = i.id;
      this.problemreason_value = [];

      for (var l = 0; l < i.tab.length; l++) {
        // console.log(i.tab[l].id);
        this.problemreason_value.push(i.tab[l].id);
      }
    },
    prolome: function prolome() {
      mark({
        id: this.markid,
        tab: this.problemreason_value.join(",")
      }).then(function (res) {});
      this.pageindex = 1;
      this.getData(); // this.changepage(1)
      // this.getpage(pageindex)

      this.dialogTableVisible = false;
    },
    problemList: function problemList() {
      var _this8 = this;

      problemReason().then(function (res) {
        // console.log(res.msg)
        _this8.problemreason = res.msg;
      });
    },
    //搜索
    search: function search(val) {
      var _this9 = this;

      this.pageindex = 1;

      if (val) {
        gamePending().then(function (res) {
          if (res.state === "1") {
            var newarr = res.msg.filter(function (item) {
              if (item.name.indexOf(val) == 0 && item.name.length == val.length) {
                return item;
              }
            });

            if (newarr.length > 0) {
              _this9.getpage(newarr);
            } else {
              _this9.$Message.warning("没有找到匹配结果");
            }
          }
        });
      } else {
        this.$Message.warning("请输入搜索内容");
      }
    },
    changepage: function changepage(index) {
      var _start = (index - 1) * this.pagesize;

      var _end = index * this.pagesize;

      this.datalist = this.totaldatalist.slice(_start, _end);
      var scrollTop = document.documentElement.scrollTop || document.body.scrollTop;
      this.$refs.marilyn_tab.scrollTop;
    },
    editEvent: function editEvent(e) {
      this.editpingtaivalue.push({
        name: "android",
        value: e.android
      });
      this.editpingtaivalue.push({
        name: "ios",
        value: e.ios
      });
      this.editpingtaivalue.push({
        name: "wechat",
        value: e.wechat
      });

      if (e.android == 1) {
        this.editPTvalue.push("android");
      }

      if (e.ios == 1) {
        this.editPTvalue.push("ios");
      }

      if (e.wechat == 1) {
        this.editPTvalue.push("wechat");
      }

      this.proId = "";
      this.labelId = [];
      this.currentEdit = e;
      this.editModel = true;
      this.typeId = e.type; // console.log(this.typeId);

      for (var i = 0; i < e.label.length; i++) {
        this.labelId.push(e.label[i].id);
      }

      this.classId = e.class.id;
    },
    uploadSuccess: function uploadSuccess(res, file) {},
    handleUpload: function handleUpload(file) {
      var self = this;
      var reader = new FileReader(),
          img = new Image();
      reader.readAsDataURL(file);

      reader.onload = function (e) {
        self.marilynNewImg = e.target.result;
      };

      this.file = file;
    },
    zipUpload: function zipUpload(file) {
      var _this10 = this;

      this.zipfileUrl = "";
      var btn = document.querySelector(".btn");
      var btn_edit = document.querySelector("#btn_edit");
      btn.disabled = true;
      btn_edit.disabled = true;
      var a = 0;
      var jindu = setInterval(function () {
        a++;
        _this10.uploadtitle = "已上传" + a + "%";

        if (_this10.zipfileUrl) {
          _this10.uploadtitle = "上传完成";
          btn.disabled = false;
          btn_edit.disabled = false;
          clearTimeout(jindu);
        }

        if (a == 99) {
          clearTimeout(jindu);
          var newtime = setInterval(function () {
            _this10.uploadtitle = "已上传99%";

            if (_this10.zipfileUrl) {
              _this10.uploadtitle = "上传完成";
              btn.disabled = false;
              btn_edit.disabled = false;
              clearTimeout(newtime);
            }
          }, 100);
        }
      }, 700);

      if (file.type.indexOf("zip") > -1) {
        this.uploaded = true;
        this.zipfile = file;
        var data = new FormData(); // console.log(data)

        var option = {
          method: "post",
          body: data
        };
        data.append("file", this.zipfile);
        fetch(zipuploadUrl, option).then(function (res) {
          return res.json();
        }).then(function (resJson) {
          // console.log(resJson)
          // console.log("dcdc")
          if (resJson.state === "1") {
            _this10.zipfileUrl = resJson.msg; //  console.log('上传 '+ this.zipfileUrl)
          }
        });
      }
    },
    modifyUpload: function modifyUpload(e) {
      var self = this;
      this.currentImg = e.currentTarget.files[0];
      var reader = new FileReader(),
          img = new Image();
      reader.readAsDataURL(this.currentImg);

      reader.onload = function (e) {
        // self.marilynAddImg = e.target.result
        document.querySelector(".curtimg").src = e.target.result;
      };
    },
    addGame: function addGame() {
      var _this11 = this;

      if (this.marilynType == "0") {
        this.marilynUrl = this.zipfileUrl;
      }

      var data = new FormData();
      data.append("file", this.file);
      data.append("name", this.marilynName);
      data.append("alias", this.marilynAlias);
      data.append("url", this.marilynUrl);
      data.append("cId", this.marilynSelect);

      if (this.marilynTag) {
        data.append("label", this.marilynTag.join(","));
      }

      data.append("type", this.marilynType);
      data.append("screen", this.marilyndir);
      data.append("recommend", this.marilyncom);
      data.append("weight", this.marilynnum);
      var option = {
        method: "post",
        body: data
      };
      fetch(gameuploadUri, option).then(function (res) {
        return res.json();
      }).then(function (resJson) {
        if (resJson.state === "1") {
          _this11.$Message.success(resJson.msg);

          _this11.file = "";
          _this11.marilynName = "";
          _this11.marilynAlias = "";
          _this11.marilynUrl = "";
          _this11.marilynSelect = "";
          _this11.marilynTag = "";
          _this11.marilynNewImg = "";

          _this11.getData();

          _this11.addCard = false;
        } else {
          _this11.$Message.error(resJson.msg);
        }
      });
    },
    toolDelete: function toolDelete() {
      var _this12 = this;

      gameDelete({
        id: this.currentDelete
      }).then(function (res) {
        if (res.state === "1") {
          _this12.deleteModel = false;

          _this12.$Message.success(res.msg);

          _this12.getData();
        } else {
          _this12.$Message.error(res.msg);
        }
      });
    },
    toolEdit: function toolEdit() {
      var _this13 = this;

      var data = new FormData();

      if (this.editPTvalue.indexOf("android") > -1) {
        data.append("android", "1");
      } else {
        data.append("android", "0");
      }

      if (this.editPTvalue.indexOf("ios") > -1) {
        data.append("ios", "1");
      } else {
        data.append("ios", "0");
      }

      if (this.editPTvalue.indexOf("wechat") > -1) {
        data.append("wechat", "1");
      } else {
        data.append("wechat", "0");
      }

      if (this.currentImg) {
        data.append("file", this.currentImg);
      }

      data.append("id", this.currentEdit.id);
      data.append("name", this.currentEdit.name);
      data.append("alias", this.currentEdit.alias);
      data.append("cId", this.classId);
      data.append("label", this.labelId.join(","));
      data.append("type", this.typeId);
      data.append("url", this.currentEdit.url);
      data.append("recommend", this.currentEdit.recommend);
      data.append("screen", this.currentEdit.screen);
      data.append("weight", this.currentEdit.weight);
      data.append("proId", this.procon);
      var option = {
        method: "post",
        body: data
      };
      fetch(gameUpdate, option).then(function (res) {
        return res.json();
      }).then(function (resJson) {
        if (resJson.state === "1") {
          _this13.editModel = false;

          _this13.$Message.success(resJson.msg);

          _this13.getData();
        } else {
          _this13.$Message.error(resJson.msg);
        }
      });
    }
  },
  watch: {
    marilynType: function marilynType(value) {
      if (value != 0) {
        document.querySelector(".btn").disabled = false;
      } else {
        if (this.uploadtitle != "上传完成") {
          document.querySelector(".btn").disabled = true;
        }
      }
    },
    addCard: function addCard(value) {
      this.btnAdd = true;
    },
    editModel: function editModel(value) {
      this.btnEdit = true;

      if (!value) {
        this.editPTvalue = [];
        this.editpingtaivalue = [];
      }
    }
  }
};
